const zenroom = global.get("zenroom");

const sign_script = `
Scenario eddsa: sign a graph query
Given I have a 'base64' named 'gql'
Given I have a 'keyring'

When I create the eddsa signature of 'gql'
And I create the hash of 'gql'

Then print 'eddsa signature' as 'base64'
Then print 'gql' as 'base64'
Then print 'hash' as 'hex'
`;

// Create the JSON body to be sent to the back-end
const body = JSON.stringify({
    query: msg.sign.query,
    variables: msg.variables
});


msg.sign.body = body;

const zenKeys = JSON.stringify({keyring: {eddsa: msg.sign.eddsa}});

msg.sign.zenKeys = zenKeys;

// Encode the JSON body as base64
const zenData = JSON.stringify({gql: Buffer.from(body, 'utf8').toString('base64')})

msg.sign.zenData = zenData;

let res = await zenroom.zencode_exec(sign_script, { data: msg.sign.zenData, keys: msg.sign.zenKeys})
            .then((result) => {
                console.log("Msg : ", result.result);
                const res = JSON.parse(result.result);
                console.log("Msg signature: ", res);
                return res;
            }).catch((error) => {
                console.error("Error in zenroom sign function: ", error);
                return error;

            });

msg.sign.signature = res;
return msg;

// Reset the headears
msg.headers = {};
msg.headers['content-type'] = 'application/json';

msg.headers['zenflows-sign'] = msg.sign.signature.eddsa_signature;
msg.headers['zenflows-user'] = msg.sign.username;
msg.headers['zenflows-hash'] = msg.sign.signature.hash;

msg.url = msg.payload.endpoint;
msg.method = 'POST';


// This is going to destroy the msg.payload!!!
// msg.payload = msg.sign.body;
msg.payload = msg.sign.body;

return msg;

// Send message from the queue
let queue = msg.queue || [];

if (queue.length > 0) {
    let next = queue.shift();
    msg.queue = queue;
    // Reset the payload since we are looping
    msg.payload = {};
    for (let field of Object.keys(next)) {
        if (field == 'url'){
            // This is the endpoint to contact
            msg.url = next[field];
        }else if (typeof(next[field]) == "object" && 'var_name' in next[field]){
            // This is the variable and optional label that we need to retrieve
            // from the flow environment
            var flowvar = msg.flowvar[next[field]['var_name']];
            if ('var_label' in next[field]){
                // Only the particular subfield to be used
                if( Array.isArray(next[field]['var_label'])){
                    for (let label of next[field]['var_label']) {
                        flowvar = flowvar[label];
                    }
                    msg.payload[field] = flowvar;
                }else{
                    msg.payload[field] = flowvar[next[field]['var_label']];
                }

            }else{
                // All variable to be used
                msg.payload[field] = flowvar;
            }
        }else{
            // Static scalar value
            msg.payload[field] = next[field];
        }
    }
    return [msg,null];  // send next message
} else {
    node.status({fill:"green", shape:"dot", text:"All messages sent"});
    return [null,msg];
}

const user_ret = msg.payload.result.user || {};
const user_data_ret = msg.payload.result["user_data"] || {};
const users_data_ret = msg.payload.result["users_data"] || {};
const locs_data_ret = msg.payload.result["locs_data"] || {};
const units_data_ret = msg.payload.result["units_data"] || {};
const res_spec_data_ret = msg.payload.result["res_spec_data"] || {};
const res_data_ret = msg.payload.result["res_data"] || {};
const process_data_ret = msg.payload.result["process_data"] || {};
const action_ret = msg.payload.result["action"] || undefined;

var updates = 0;

if ((Object.keys(user_data_ret).length !== 0) && (Object.keys(users_data_ret).length !== 0)) {
    throw "user_data and users_data returned";
}
if (Object.keys(locs_data_ret).length !== 0) {
    if (user_ret == undefined) {
        throw "locs_data is returned but no user is given";
    } else {
        var locs_data = msg.flowvar["locs_data"];
        locs_data[user_ret] = locs_data_ret[user_ret];
        msg.flowvar["locs_data"] = locs_data;
        updates += 1;
        node.log("locs_data updated");
    }
}

if (Object.keys(units_data_ret).length !== 0) {
    const unit_name_ret = msg.payload.result["name"] || undefined;
    if (unit_name_ret == undefined) {
        throw "units_data is returned but no unit name is given";
    } else {
        var units_data = msg.flowvar["units_data"];
        units_data[unit_name_ret] = units_data_ret[unit_name_ret];
        msg.flowvar["units_data"] = units_data;
        updates += 1;
        node.log("units_data updated");
    }
}
if (Object.keys(res_spec_data_ret).length !== 0) {
    const res_spec_name_ret = msg.payload.result["name"] || undefined;
    if (res_spec_name_ret == undefined) {
        throw "res_spec_data is returned but no res spec name is given";
    } else {
        var res_spec_data = msg.flowvar["res_spec_data"];
        res_spec_data[res_spec_name_ret] = res_spec_data_ret[res_spec_name_ret];
        msg.flowvar["res_spec_data"] = res_spec_data;
        updates += 1;
        node.log("res_spec_data updated");
    }
}
if (Object.keys(res_data_ret).length !== 0) {
    const res_name_ret = msg.payload.result["res_name"] || undefined;
    const event_seq_ret = msg.payload.result["event_seq"] || undefined;
    if (event_seq_ret == undefined || event_seq_ret.length != 1) {
        throw "No or more than one event returned" + event_seq_ret;
    }

    if (res_name_ret == undefined) {
        throw "res_data is returned but no res name is given";
    }
    var event_seq = msg.flowvar["event_seq"];
    event_seq.push(event_seq_ret[0]);
    msg.flowvar["event_seq"] = event_seq;

    var res_data = msg.flowvar["res_data"];
    res_data[res_name_ret] = res_data_ret[res_name_ret];
    msg.flowvar["res_data"] = res_data;
    updates += 1;
    node.log("res_data updated");
}
if (Object.keys(process_data_ret).length !== 0) {
    const process_name_ret = msg.payload.result["process_name"] || undefined;
    if (process_name_ret == undefined) {
        throw "process_data is returned but no process name is given";
    }
    var process_data = msg.flowvar["process_data"];
    process_data[process_name_ret] = process_data_ret[process_name_ret];
    msg.flowvar["process_data"] = process_data;
    updates += 1;
    node.log("process_data updated");
}
if (Object.keys(user_data_ret).length !== 0) {
    if (user_ret == undefined) {
        throw "user_data is returned but no user is given";
    } else {
        var users_data = msg.flowvar["users_data"];
        users_data[user_ret] = user_data_ret;
        msg.flowvar["users_data"] = users_data;
        updates += 1;
        node.log("users_data updated");
    }
}
if (Object.keys(users_data_ret).length !== 0) {
    if (user_ret == undefined) {
        throw "users_data is returned but no user is given";
    } else {
        var users_data = msg.flowvar["users_data"];
        users_data[user_ret] = users_data_ret[user_ret];
        msg.flowvar["users_data"] = users_data;
        updates += 1;
        node.log("users_data updated");
    }
}
if (action_ret != undefined) {
    const existing_res_ret = msg.payload.result["existing_res"] || {};
    const new_res_ret = msg.payload.result["new_res"] || {};

    if (Object.keys(existing_res_ret).length !== 0) {
        var res_data = msg.flowvar["res_data"];
        res_data[existing_res_ret['name']] = existing_res_ret;
        msg.flowvar["res_data"] = res_data;
        node.log("existing res_data in event updated");
    }
    if (Object.keys(new_res_ret).length !== 0) {
        var res_data = msg.flowvar["res_data"];
        res_data[new_res_ret['name']] = new_res_ret;
        msg.flowvar["res_data"] = res_data;
        node.log("new res_data in event updated");
    }

    const rec_event_ret = msg.payload.result["rec_event"] || undefined;
    if (rec_event_ret == undefined) {
        throw "action provided but no event recorded";
    }
    var event_seq = msg.flowvar["event_seq"];
    if (!'action' in rec_event_ret){
        throw "No action recorded in event";
    }
    var event = {};
    for (let key of Object.keys(rec_event_ret['action'])){
        event[key] = rec_event_ret['action'][key];
    }
    event_seq.push(event);
    if ('process' in rec_event_ret){
        var event = {};
        for (let key of Object.keys(rec_event_ret['process'])){
            event[key] = rec_event_ret['process'][key];
        }
        event_seq.push(event);
    }
    msg.flowvar["event_seq"] = event_seq;
    node.log("event added");
    updates += 1;
}

if (updates != 1) {
    throw "Incorrect number of updates performed: " + updates;
}
return msg;

