// Send message from the queue
let queue = flow.get("eventQueue") || [];

if (queue.length > 0) {
    let next = queue.shift();
    flow.set("eventQueue", queue);
    // Reset the payload since we are looping
    msg.payload = {};
    for (let field of Object.keys(next)) {
        if (field == 'url'){
            // This is the endpoint to contact
            msg.url = next[field];
        }else if (typeof(next[field]) == "object" && 'var_name' in next[field]){
