    if ('process' in rec_event_ret){
        var event = {};
        for (let key of Object.keys(rec_event_ret['process'])){
            event[key] = rec_event_ret['process'][key];
        }
        event_seq.push(event);
    }
    flow.set("event_seq", event_seq);
    node.log("event added");
    updates += 1;
}

if (updates != 1) {
    throw "Incorrect number of updates performed: " + updates;
}
